import React, { Component } from "react"; 
import ReactDOM from "react-dom";
//import components 
import NewPayment from './AddNewPayment.js';
import ExistingPayment from './ExistingPayment.js';

//import logo
import logo from '../../assets/donate-differently-logo.png';


import StripeCheckout from "react-stripe-checkout";
import axios from "axios";
import { toast } from "react-toastify";

export default class Payment extends Component {
    render() {
        return(
            <div className='payment'>
                <img src={logo} height="150px" alt="logo"/>
                <div className='add-new-payment'>
                    <StripeCheckout/>
                    <NewPayment/>
                </div>
                <div className='saved-payment'>
                    <ExistingPayment/>
                </div>
            </div>
        );
    }
}
